import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import About from './pages/About';
import Services from './pages/Services';
import Contact from './pages/Contact';
import Webservice from './pages/Webservice';
import Photography from './pages/Photography';
import Videography from './pages/Videography';
import Quote from './pages/Quote';

// Helper component to update the title dynamically
function DynamicTitle() {
  const location = useLocation();

  useEffect(() => {
    // Define titles for each route
    const titles = {
      '/': 'Home - HOMIEZ',
      '/about': 'About Us - HOMIEZ',
      '/services': 'Our Services - HOMIEZ',
      '/contact': 'Contact Us - HOMIEZ',
      '/web-services': 'Web Services - HOMIEZ',
      '/photography': 'Photography - HOMIEZ',
      '/videography': 'Videography - HOMIEZ',
      '/Quote': 'Quote - HOMIEZ',
    };

    // Set the document title based on the current route
    document.title = titles[location.pathname] || 'HOMIEZ';
  }, [location]);

  return null; // This component only updates the title; it doesn't render anything
}

function App() {
  return (
    <Router>
      <Navbar />
      <DynamicTitle />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/services" element={<Services />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="/web-services" element={<Webservice />} />
        <Route path="/photography" element={<Photography />} />
        <Route path="/videography" element={<Videography />} />
        <Route path="/Quote" element={<Quote />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
